export interface NameServiceProfile {
    /** address of the wallet */
    address: string;
    /** name associated with the address */
    name: string;
    /** logo Arweave TxID */
    logo?: string;
}
/**
 * Name Service profile hook: looks up a wallet's profile in ArNS and ANS
 */
export default function useNameService({ useArNS, useAns }: {
    useArNS: boolean;
    useAns: boolean;
}): NameServiceProfile | undefined;
